   private void btnRegisterActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRegisterActionPerformed

      String us = txtRegUsername.getText();
      String pw = String.valueOf(txtRegPassword.getPassword());

      if (us.isBlank() || pw.isBlank()) {
         lblError.setText("Please fill in the columns");
      } else {
         User user = new User(us, pw);
         user.setUsername(us);
         user.setPassword(pw);

         try {
            FileWriter fw = new FileWriter("user.txt", true);
            BufferedWriter bw = new BufferedWriter(fw);
            x = new Scanner(new File("user.txt"));
            boolean found = false;
            while (x.hasNext() && !found) {
               String tempUsername = x.nextLine();
               String tempPassword = x.nextLine();
               if (tempUsername.trim().equals(user.getUsername().trim()) 
                       && tempPassword.trim().equals(user.getPassword().trim())) {
                  found = true;
               }
            }
            if (found == true) {
               lblError.setText("User already exists");
            } else {
               bw.write(user.getUsername());
               bw.newLine();
               bw.write(user.getPassword());
               bw.newLine();
               bw.close();
               lblError.setText("Data has been registered");
               txtRegUsername.setText("");
               txtRegPassword.setText("");
            }
         } catch (IOException ex) {
            lblError.setText("Error has occured : " + ex);
         }
      }

   }//GEN-LAST:event_btnRegisterActionPerformed

